import { Page, Locator } from '@playwright/test';
import InventoryPage from './InventoryPage';

/**
 * LoginPage encapsulates interactions with the login screen.
 * Single Responsibility: only exposes actions & assertions relevant to the login page.
 */
export default class LoginPage {
  private readonly page: Page;
  private readonly usernameInput: Locator;
  private readonly passwordInput: Locator;
  private readonly loginButton: Locator;
  private readonly errorMessage: Locator;

  constructor(page: Page) {
    this.page = page;
    this.usernameInput = page.locator('#user-name');
    this.passwordInput = page.locator('#password');
    this.loginButton = page.locator('#login-button');
    this.errorMessage = page.locator('[data-test="error"]');
  }

  /**
   * Navigate to the login page. Uses baseURL from Playwright config when calling `page.goto` with '/'.
   */
  async goto(): Promise<void> {
    await this.page.goto('/');
    await this.usernameInput.waitFor({ state: 'visible' });
  }

  /**
   * Perform login with provided credentials. Returns InventoryPage on success.
   */
  async login(username: string, password: string): Promise<InventoryPage> {
    await this.usernameInput.fill(username);
    await this.passwordInput.fill(password);
    await Promise.all([
      this.page.waitForNavigation({ waitUntil: 'networkidle' }),
      this.loginButton.click()
    ]);
    return new InventoryPage(this.page);
  }

  /**
   * Convenience method for the standard user credentials used in tests.
   */
  async loginAsStandardUser(): Promise<InventoryPage> {
    return this.login('standard_user', 'secret_sauce');
  }

  async getErrorMessageText(): Promise<string> {
    return this.errorMessage.textContent();
  }

  async isLoginButtonEnabled(): Promise<boolean> {
    return this.loginButton.isEnabled();
  }
}

import { Page, Locator, expect } from '@playwright/test';

/**
 * InventoryPage represents the page users land on after successful authentication.
 * Encapsulates assertions and interactions with the inventory list.
 */
export default class InventoryPage {
  private readonly page: Page;
  private readonly inventoryContainer: Locator;
  private readonly title: Locator;

  constructor(page: Page) {
    this.page = page;
    this.inventoryContainer = page.locator('.inventory_list');
    this.title = page.locator('.title');
  }

  async isLoaded(): Promise<boolean> {
    // Ensure that the inventory list is visible
    return this.inventoryContainer.isVisible();
  }

  async expectIsLoaded(): Promise<void> {
    await expect(this.inventoryContainer).toBeVisible();
    await expect(this.title).toHaveText('Products');
  }

  async getUrl(): Promise<string> {
    return this.page.url();
  }
}

import { test, expect } from '@playwright/test';
import LoginPage from '../src/pages/LoginPage';

/**
 * Test suite for login functionality using Page Object Model.
 * This test logs in with the provided standard credentials and verifies success.
 */
test.describe('Login Page - POM', () => {
  test('should login successfully with standard_user / secret_sauce', async ({ page }) => {
    const loginPage = new LoginPage(page);

    // Arrange - navigate to the app
    await loginPage.goto();

    // Act - login with the standard user
    const inventoryPage = await loginPage.loginAsStandardUser();

    // Assert - inventory page loaded
    await inventoryPage.expectIsLoaded();

    // Additionally assert URL is the expected inventory page
    const url = await inventoryPage.getUrl();
    expect(url).toContain('/inventory.html');
  });

  test('should show error with invalid credentials', async ({ page }) => {
    const loginPage = new LoginPage(page);
    await loginPage.goto();

    // invalid login
    await loginPage.login('invalid_user', 'bad_password');

    // Expect an error message to be visible
    const errorText = await loginPage.getErrorMessageText();
    expect(errorText).toBeTruthy();
    expect(errorText.toLowerCase()).toContain('username and password do not match');
  });
});

