import { test, expect } from '@playwright/test';
import { LoginPage } from '../src/pages/LoginPage';
import { ProductsPage } from '../src/pages/ProductsPage';

test.describe('SauceDemo negative login tests', () => {
  test('shows error for invalid credentials', async ({ page }) => {
    const login = new LoginPage(page);
    const products = new ProductsPage(page);

    await login.goto();

    // Attempt with invalid username and password
    await login.login('invalid_user', 'wrong_password');

    // Expect to stay on login page and show proper error message
    const errorText = await login.getErrorText();
    expect(errorText).toContain('Username and password do not match any user');

    // Ensure we did not navigate to products page
    expect(await products.isOpen()).toBeFalsy();
  });

  test('locked out user cannot login and sees locked out message', async ({ page }) => {
    const login = new LoginPage(page);
    const products = new ProductsPage(page);

    await login.goto();

    // locked_out_user is a known account on saucedemo
    await login.login('locked_out_user', 'secret_sauce');

    const errorText = await login.getErrorText();
    expect(errorText).toContain('Sorry, this user has been locked out.');

    // Verify products page is not accessible
    expect(await products.isOpen()).toBeFalsy();
  });
});

