namespace Proj1756843281583.Pages
{
    public abstract class BasePage
    {
        protected readonly IWebDriver Driver;
        protected readonly WebDriverWait Wait;

        protected BasePage(IWebDriver driver, TimeSpan? timeout = null)
        {
            Driver = driver ?? throw new ArgumentNullException(nameof(driver));
            Wait = new WebDriverWait(Driver, timeout ?? TimeSpan.FromSeconds(10));
        }

        protected IWebElement WaitForElementVisible(By by)
        {
            return Wait.Until(ExpectedConditions.ElementIsVisible(by));
        }

        protected void Click(By by)
        {
            Wait.Until(ExpectedConditions.ElementToBeClickable(by)).Click();
        }

        protected void EnterText(By by, string text)
        {
            var el = WaitForElementVisible(by);
            el.Clear();
            el.SendKeys(text);
        }

        public string GetText(By by)
        {
            try
            {
                return WaitForElementVisible(by).Text;
            }
            catch (WebDriverTimeoutException)
            {
                return string.Empty;
            }
        }
    }
}

