namespace Proj1756843281583.Driver
{
    /// <summary>
    /// Responsible for creating configured IWebDriver instances.
    /// Single Responsibility: only creates and configures web drivers.
    /// </summary>
    public static class WebDriverFactory
    {
        /// <summary>
        /// Create a ChromeDriver with safe defaults. Headless by default.
        /// </summary>
        /// <param name="headless">Run Chrome headless when true.</param>
        /// <returns>IWebDriver instance</returns>
        public static IWebDriver CreateChrome(bool headless = true)
        {
            var options = new ChromeOptions();
            // Common stable options
            options.AddArgument("--no-sandbox");
            options.AddArgument("--disable-dev-shm-usage");
            options.AddArgument("--disable-gpu");

            if (headless)
            {
                // Use "new" headless mode where available
                options.AddArgument("--headless=new");
            }

            // Instantiate ChromeDriver (Selenium.WebDriver.ChromeDriver NuGet provides the binary)
            return new ChromeDriver(options);
        }
    }
}

